// Example of a basic concept implementation
function demonstrateConcept(input) {
  const result = processInput(input);
  return formatOutput(result);
}

// This pattern will be used throughout the course

// Applying our concepts to a real problem
function solveRealProblem(input) {
  // Break down the problem
  const parts = decompose(input);

  // Apply the appropriate concept to each part
  const results = parts.map(part => applyConcept(part));

  // Combine the results
  return integrate(results);
}
